									exports[letFunctionName](letFunctionAgrvs[0], letFunctionAgrvs[1], letFunctionAgrvs[2], letFunctionAgrvs[3], letFunctionAgrvs[4], letFunctionAgrvs[5], letFunctionAgrvs[6], letFunctionAgrvs[7], letFunctionAgrvs[8]);
